import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { fetchSubCategories } from "../contentfulClient";

export default function SubCategory() {
  const { mainCategoryId } = useParams();
  const [subCategories, setSubCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const getSubCategories = async () => {
      try {
        const subCategoriesData = await fetchSubCategories(mainCategoryId);
        setSubCategories(subCategoriesData);
      } catch (error) {
        console.error("Alt kategoriler al覺namad覺:", error);
      }
    };

    getSubCategories();
  }, [mainCategoryId]);

  return (
    <section className="bg-white text-black py-16 px-10">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-4xl md:text-5xl font-extrabold leading-snug mb-8">
          SUB CATEGORIES
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {subCategories.map((subCategory) => (
            <div
              key={subCategory.sys.id}
              className="bg-gray-100 shadow-md p-6 rounded-lg cursor-pointer"
              onClick={() => navigate(`/products/${subCategory.sys.id}`)}
            >
              <img
                src={subCategory.fields.sub_category_image.fields.file.url}
                alt={subCategory.fields.sub_categories_name}
                className="w-full h-48 object-cover rounded-md mb-4"
              />
              <h3 className="text-2xl font-bold mb-2">
                {subCategory.fields.sub_categories_name}
              </h3>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
