import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchProducts } from "../contentfulClient"; // Ürünleri getiren fonksiyon

const Products = () => {
  const { id } = useParams(); // Alt kategori ID'sini URL'den alın
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const fetchedProducts = await fetchProducts(id); // Alt kategoriye ait ürünleri al
        setProducts(fetchedProducts);
      } catch (err) {
        console.error("Ürünler alınamadı:", err);
        setError("Ürünler alınırken bir hata oluştu.");
      }
    };

    getProducts();
  }, [id]);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8">Products</h1>
      {error && <p className="text-red-500">{error}</p>}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {products.map((product) => (
          <div key={product.sys.id} className="bg-gray-100 shadow-md p-6 rounded-lg">
            <img
              src={product.fields.product_image.fields.file.url}
              alt={product.fields.product_name}
              className="w-full h-48 object-cover rounded-md mb-4"
            />
            <h3 className="text-2xl font-bold mb-2">{product.fields.product_name}</h3>
            <p>{product.fields.product_description?.content[0]?.content[0]?.value}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
