import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchSubCategories } from "../contentfulClient"; // Alt kategorileri getiren fonksiyon

export default function SubCategory() {
  const { id } = useParams(); // URL'den gelen ID
  const [subCategories, setSubCategories] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getSubCategories = async () => {
      try {
        const fetchedSubCategories = await fetchSubCategories(id); // Alt kategorileri al
        setSubCategories(fetchedSubCategories);
      } catch (err) {
        console.error("Alt kategoriler alınamadı:", err);
        setError("Veriler alınırken bir hata oluştu.");
      }
    };

    getSubCategories();
  }, [id]);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8">Sub Categories</h1>
      {error && <p className="text-red-500">{error}</p>}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {subCategories.map((subCategory) => (
          <div key={subCategory.sys.id} className="bg-gray-100 shadow-md p-6 rounded-lg">
            <img
              src={subCategory.fields.sub_category_image.fields.file.url}
              alt={subCategory.fields.sub_categories_name}
              className="w-full h-48 object-cover rounded-md mb-4"
            />
            <h3 className="text-2xl font-bold mb-2">
              {subCategory.fields.sub_categories_name}
            </h3>
          </div>
        ))}
      </div>
    </div>
  );
}
