import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchSubCategories } from "../contentfulClient";

const SubCategory = () => {
  const { id } = useParams(); // Main Category ID
  const [subCategories, setSubCategories] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getSubCategories = async () => {
      try {
        const data = await fetchSubCategories(id); // Main Category ID'yi kullanarak alt kategorileri çek
        setSubCategories(data);
      } catch (err) {
        setError("Alt kategoriler alınırken bir hata oluştu.");
      }
    };

    getSubCategories();
  }, [id]);

  return (
    <div className="bg-white text-black py-16 px-10">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-4xl md:text-5xl font-extrabold leading-snug mb-8">
          Sub Categories
        </h2>
        {error && <p className="text-red-500">{error}</p>}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {subCategories.map((subCategory) => (
            <div
              key={subCategory.sys.id}
              className="bg-gray-100 shadow-md p-6 rounded-lg"
            >
              <img
                src={subCategory.fields.sub_category_image.fields.file.url}
                alt={subCategory.fields.sub_categories_name}
                className="w-full h-48 object-cover rounded-md mb-4"
              />
              <h3 className="text-2xl font-bold mb-2">
                {subCategory.fields.sub_categories_name}
              </h3>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SubCategory;
