import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchProducts } from "../contentfulClient";
import { documentToReactComponents } from "@contentful/rich-text-react-renderer";

export default function Products() {
  const { id } = useParams(); // Sub Category ID'yi al
  const [products, setProducts] = useState([]); // Ürünleri tutacak state
  const [error, setError] = useState(null); // Hataları tutacak state

  useEffect(() => {
    const getProducts = async () => {
      try {
        const fetchedProducts = await fetchProducts(id); // Sub Category ID'yi kullan
        setProducts(fetchedProducts); // Gelen ürünleri state'e kaydet
      } catch (error) {
        console.error("Ürünler alınamadı:", error);
        setError("Ürünler alınırken bir hata oluştu.");
      }
    };

    getProducts(); // Ürünleri almak için fonksiyonu çağır
  }, [id]);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8">Products</h1>
      {error && <p className="text-red-500">{error}</p>}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {products.map((product) => {
          const imageUrl =
            product.fields.product_image?.fields?.file?.url || ""; // Güvenli şekilde image URL alınıyor

            return (
                <div key={product.sys.id} className="bg-gray-100 shadow-md p-6 rounded-lg">
                  {product.fields.product_image && product.fields.product_image.fields?.file?.url ? (
                    <img
                      src={product.fields.product_image.fields.file.url} // Ürün resmi
                      alt={product.fields.product_name || "No Name"} // Ürün adı
                      className="w-full h-48 object-cover rounded-md mb-4"
                    />
                  ) : (
                    <div className="w-full h-48 bg-gray-200 rounded-md mb-4 flex items-center justify-center">
                      <span>No Image</span>
                    </div>
                  )}
                  <h3 className="text-2xl font-bold mb-2">{product.fields.product_name || "No Name"}</h3>
                  {product.fields.product_description && (
                    <div>
                      {documentToReactComponents(product.fields.product_description)} {/* RichText içerik */}
                    </div>
                  )}
                </div>
              );

        })}
      </div>
    </div>
  );
}
