import React, { useEffect, useState } from "react";
import { fetchMainCategories } from "../contentfulClient"; // Yukarıdaki fonksiyonun import edildiği yer

export default function Projects() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    async function getCategories() {
      const data = await fetchMainCategories();
      setCategories(data);
    }
    getCategories();
  }, []);

  return (
    <section className="bg-white text-black py-16 px-10">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-4xl md:text-5xl font-extrabold leading-snug mb-8">
          PRODUCT CATEGORIES
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {categories.map((category) => {
            const { fields } = category;
            return (
              <div
                key={category.sys.id}
                className="bg-gray-100 shadow-md p-6 rounded-lg"
              >
                {fields.main_category_image && (
                  <img
                    src={`https:${fields.main_category_image.fields.file.url}`}
                    alt={fields.main_category_name}
                    className="w-full h-48 object-cover rounded-md mb-4"
                  />
                )}
                <h3 className="text-2xl font-bold mb-2">
                  {fields.main_category_name}
                </h3>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}
