import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchProducts } from "../contentfulClient"; // Ürünleri getiren fonksiyon

export default function Products() {
  const { id } = useParams(); // URL'den gelen Sub Category ID
  const [products, setProducts] = useState([]); // Ürünleri tutacak state
  const [error, setError] = useState(null); // Hataları tutacak state

  useEffect(() => {
    const getProducts = async () => {
      try {
        const fetchedProducts = await fetchProducts(id); // Sub Category ID'yi kullan
        setProducts(fetchedProducts); // Gelen ürünleri state'e kaydet
      } catch (error) {
        console.error("Ürünler alınamadı:", error);
        setError("Ürünler alınırken bir hata oluştu.");
      }
    };

    getProducts(); // Ürünleri almak için fonksiyonu çağır
  }, [id]); // Sub Category ID değiştiğinde yeniden çalışır

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8">Products</h1>
      {error && <p className="text-red-500">{error}</p>}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {products.map((product) => (
          <div key={product.sys.id} className="bg-gray-100 shadow-md p-6 rounded-lg">
            <img
              src={product.fields.product_image.fields.file.url} // Ürün resmi
              alt={product.fields.product_name} // Ürün adı
              className="w-full h-48 object-cover rounded-md mb-4"
            />
            <h3 className="text-2xl font-bold mb-2">{product.fields.product_name}</h3>
            <p>{product.fields.product_description}</p> {/* Ürün açıklaması */}
          </div>
        ))}
      </div>
    </div>
  );
}
