import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchProducts } from "../contentfulClient";

export default function Products() {
  const { subCategoryId } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const productsData = await fetchProducts(subCategoryId);
        setProducts(productsData);
      } catch (error) {
        console.error("Ürünler alınamadı:", error);
      }
    };

    getProducts();
  }, [subCategoryId]);

  return (
    <section className="bg-white text-black py-16 px-10">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-4xl md:text-5xl font-extrabold leading-snug mb-8">
          PRODUCTS
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {products.map((product) => (
            <div key={product.sys.id} className="bg-gray-100 shadow-md p-6 rounded-lg">
              <img
                src={product.fields.product_image.fields.file.url}
                alt={product.fields.product_name}
                className="w-full h-48 object-cover rounded-md mb-4"
              />
              <h3 className="text-2xl font-bold mb-2">{product.fields.product_name}</h3>
              <p className="text-sm">{product.fields.product_description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
