import { createClient } from "contentful";

const client = createClient({
  space: process.env.REACT_APP_CONTENTFUL_SPACE_ID,
  accessToken: process.env.REACT_APP_CONTENTFUL_ACCESS_TOKEN,
});

export const fetchSubCategories = async (mainCategoryId) => {
  const entries = await client.getEntries({
    content_type: "sub_categories",
    "fields.main_category.sys.id": mainCategoryId,
  });
  return entries.items;
};

export const fetchProducts = async (subCategoryId) => {
  const entries = await client.getEntries({
    content_type: "products",
    "fields.sub_category.sys.id": subCategoryId,
  });
  return entries.items;
};
