import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchSubCategories } from "../contentfulClient";

const SubCategory = () => {
  const { id } = useParams(); // Sadece 'id' parametresini alın
  const [subCategories, setSubCategories] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getSubCategories = async () => {
      try {
        const data = await fetchSubCategories(id);
        setSubCategories(data);
      } catch (err) {
        setError("Alt kategoriler alınırken bir hata oluştu.");
      }
    };
    getSubCategories();
  }, [id]);

  return (
    <div>
      <h1>Sub Categories</h1>
      {error && <p>{error}</p>}
      <div>
        {subCategories.map((sub) => (
          <div key={sub.sys.id}>
            <h2>{sub.fields.sub_categories_name}</h2>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SubCategory;
