import React, { useEffect, useState } from "react";
import { createClient } from "contentful";

export default function Projects() {
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Contentful API'ye bağlanmak için client oluştur
  const client = createClient({
    space: process.env.REACT_APP_CONTENTFUL_SPACE_ID,
    accessToken: process.env.REACT_APP_CONTENTFUL_ACCESS_TOKEN,
  });

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        // Ana kategorileri getir
        const response = await client.getEntries({
          content_type: "mainCategory", // Ana kategori content type API ID'si
        });

        setCategories(response.items);
        setIsLoading(false);
      } catch (error) {
        console.error("Contentful'dan veriler alınamadı:", error);
        setIsLoading(false);
      }
    };

    fetchCategories();
  }, []);

  return (
    <section className="bg-white text-black py-16 px-10">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-4xl md:text-5xl font-extrabold leading-snug mb-8">
          PRODUCT CATEGORIES
        </h2>
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {categories.map((category) => (
              <div
                key={category.sys.id}
                className="bg-gray-100 shadow-md p-6 rounded-lg"
              >
                <img
                  src={category.fields.mainCategoryImage.fields.file.url}
                  alt={category.fields.mainCategoryName}
                  className="w-full h-48 object-cover rounded-md mb-4"
                />
                <h3 className="text-2xl font-bold mb-2">
                  {category.fields.mainCategoryName}
                </h3>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
}
