import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Header from "./components/Header";
import Projects from "./components/Projects";
import SubCategory from "./components/SubCategory"; // SubCategory bileşeni
import Products from "./components/Products";
import "./i18n";

function App() {
  return (
    <Router>
      <Routes>
        {/* Dil parametresi ile rotalar */}
        <Route path="/:lang" element={<Header />} />
        <Route path="/:lang/projects" element={<Projects />} />
        <Route path="/:lang/sub-category/:id" element={<SubCategory />} />
        <Route path="/:lang/products/:id" element={<Products />} /> {/* Yeni ürün rotası */}
import { Link } from "react-router-dom";

<ul className="absolute top-28 left-0 right-0 bg-black bg-opacity-90 text-white md:static md:flex md:space-x-8 md:bg-transparent md:text-gray-300 md:items-center">
  <li className="hover:text-white cursor-pointer px-6 py-4 border-b border-gray-500 md:border-0">
    <Link to={`/${lang}/header`}>HOME</Link>
  </li>
  <li className="hover:text-white cursor-pointer px-6 py-4 border-b border-gray-500 md:border-0">
    SERVICES
  </li>
  <li className="hover:text-white cursor-pointer px-6 py-4 border-b border-gray-500 md:border-0">
    <Link to={`/${lang}/projects`}>PROJECTS</Link>
  </li>
  <li className="hover:text-white cursor-pointer px-6 py-4 border-b border-gray-500 md:border-0">
    CONNECT
  </li>
</ul>


        {/* Varsayılan rota */}
        <Route path="/" element={<Navigate to="/en" replace />} />
      </Routes>
    </Router>

  );
}

export default App;
